import React, {Component} from 'react'
import {SUBMIT, EMPTY_FORM} from '../../Constants'
import ErrorForm from '../error/ErrorForm'

class UpdatePost extends Component {
  constructor(props){
    super(props)
    this.state = {
      tab: this.props.updatedata
    };
  }


  render() {
    return (
      <div>
        <div className='addForm'>
          <h3>Ajouter un  nouvel article</h3>
          <form onSubmit={event => this._onSubmit(event)}>
              <input
                ref={title => (this.title = title)}
                type="text"
                id="title"
                value={this.state.tab.title}
              />
              <textarea
                ref={body => (this.body = body)}
                value={this.state.tab.body}
              />
              <button className='button button__from' type="submit">{SUBMIT}</button>
            {this.state.error === true ? <ErrorForm errorMessage={EMPTY_FORM}/> : null}
          </form>
        </div>
      </div>
    )
  }
}

export default UpdatePost
