import React, {Component} from 'react'
import ArticleListItem from '../components/Article-list-item';

import {API_URL, METHODS, LIMIT} from '../Constants'


class ArticleList extends Component {


    constructor(props){
        super(props)
        this.state = {
          data: [],
          add: []
        };

        this.handleClick = this.handleClick.bind(this);
    }

    async handleClick(index) {
      const { data } = this.state;
      const response = await fetch(`${API_URL}/posts/${index}` , {
        method: METHODS.delete
      });

      if (response.status === 200) {
        const filterData = data.filter(item => item.id !== index);

        this.setState({
          data: filterData
        });
      }
    }

    addArticle() {
      fetch(`${API_URL}/posts`, {
        method: METHODS.post,
        body: JSON.stringify({
        title: 'MASSSSSHHHAAAAALLLLAAAAAAA',
        body: 'YOLLLLOOOOOOOO',
        userId: 1
      }),
      headers: {
       "Content-type": "application/json; charset=UTF-8"
      }
    })
    .then(response => response.json())
    .then(json => this.state.data.push(json))
    .then(console.log(this.state.data))
    }



    async componentDidMount () {
      try{
        const response = await fetch (`${API_URL}/posts`);
        const json = await response.json();
        this.setState({data: json})
      } catch (error){
        console.log("ERROR" + error)
      }
    }


    render() {

    if(this.state.data !== []){
      return (

        <div>
          <button onClick={() => this.addArticle()}>test</button>
          <ul>
            {console.log(this.state.data)}
              {/* {this.state.data.map(el => (
                  <ArticleListItem 
                    key={el.id} 
                    title={el.title} 
                    body={el.body} 
                    onClick={() => this.handleClick(el.id)}
                  />
              ))} */}
            </ul>    
        </div>
      )
    }
  }
}

export default ArticleList
