import React, {Component} from 'react'
import { withRouter } from 'react-router-dom'
import ErrorForm from './../error/ErrorForm'
import {SUBMIT, EMPTY_FORM, API_URL} from './../../Constants'
import {METHODS} from '../../Constants'


class ArticleList extends Component {
    constructor(props){
        super(props)
        this.state = {
        };
    }


    _onEdit = dataEdit => {
      const { 
        history: {
            location: {
              pathname
            }
        }
      } = this.props;
      const idEdit = pathname.split('/')[2];
      dataEdit.preventDefault();
      fetch(`${API_URL}/posts/${idEdit}`, {
      method: METHODS.put,
      body: JSON.stringify({
        id: +idEdit,
        title: this.title.value,
        body: this.body.value,
        userId: 1
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8"
      }
    })
    .then(response => response.json())
    .then(json =>  this.props.editItem(json))
    }

    render() {
      return (
        <div className='addForm'>
        <h3>Modifier un article</h3>
        <form onSubmit={e => this._onEdit(e)}>
            <input
              ref={title => (this.title = title)}
              type="text"
              id="title"
            />
            <textarea
              ref={body => (this.body = body)}
            />
            <button className='button button__from' type="submit">{SUBMIT}</button>
          {this.state.error === true ? <ErrorForm errorMessage={EMPTY_FORM}/> : null}
        </form>
      </div>
      )
  }
}

export default withRouter(ArticleList);
