import React, {Component} from 'react';
import PhotoListItems from '../components/PhotoListItems'
import {API_URL, LIMIT} from '../Constants'

class PhotoList extends Component {
    constructor(props){
        super(props)
        this.state = {
            data: []
        };

        this.handleClick = this.handleClick.bind(this);
    }

    async componentDidMount () {
        try{
          const response = await fetch (`${API_URL}/photos${LIMIT}`);
          const json = await response.json();
          this.setState({data: json})
        } catch (error){
          console.log("ERROR" + error)
        }
      }

    render(){
        if(this.state.data !== []){
            return (
                <div>
                   {this.state.data.map(el => (
                       <PhotoListItems key={el.id} title={el.title} url={el.url} onClick={this.handleClick(el.id)}/>
                   ))}
                </div>
              )
        }
    }
} 

export default PhotoList
