    return (
      <li>
        <h3>{title}</h3>
        <p>{body}</p>
        <div className='containerButton'>
          <button className='button' onClick={() => history.push(`/edit/${id}`)} editItem={_onEdit}>Modifier</button>
          <button className='button' onClick={() => onClick()}>Suprimer</button>
        </div>
      </li>
    )
} 

