import React, {Component} from 'react'
import { withRouter } from 'react-router-dom'
import ErrorForm from './../error/ErrorForm'
import {SUBMIT, EMPTY_FORM} from './../../Constants'
import {METHODS} from '../../Constants'


class ArticleList extends Component {
    constructor(props){
        super(props)
        this.state = {
          idEdit: 0
        };
    }

    _onEdit = dataEdit => {
      fetch('https://jsonplaceholder.typicode.com/posts/1', {
        method: METHODS.put,
        body: JSON.stringify({
          id: this.state.idEdit,
          title: dataEdit.idEdit,
          body: dataEdit.body,
          userId: 1
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8"
        }
      })
      .then(response => response.json())
      .then(json => console.log(json))
    }

    render() {
      const { 
        history: {
            location: {
              pathname
            }
        }
      } = this.props;

      const split = pathname.split('/')[2];
      this.setState({
        idEdit: split
      })
      return (
        <div className='addForm'>
        <h3>Modifier un article {split}</h3>
        <form onSubmit={e => this._onEdit(e)}>
            <input
              ref={title => (this.title = title)}
              type="text"
              id="title"
            />
            <textarea
              ref={body => (this.body = body)}
            />
            <button className='button button__from' type="submit">{SUBMIT}</button>
          {this.state.error === true ? <ErrorForm errorMessage={EMPTY_FORM}/> : null}
        </form>
      </div>
      )
  }
}

export default withRouter(ArticleList);
