import React, {Component} from 'react'
import { withRouter } from 'react-router-dom'
import ErrorForm from './../error/ErrorForm'
import {SUBMIT, EMPTY_FORM, API_URL} from './../../Constants'
import {METHODS} from '../../Constants'


class ArticleList extends Component {
    constructor(props){
        super(props)
        this.state = { };
    }

    _onSubmit = event => {
      console.log('EVENT: ', event)
      event.preventDefault()
      const title = this.title.value
      const message = this.body.value
      const body = {
        id: this.props.idEdit,
        title: title,
        body: message
      }

      fetch(` ${API_URL}/posts/${this.props.idEdit}`, {
        method: METHODS.put,
        body: JSON.stringify(body),
        headers: {
          "Content-type": "application/json; charset=UTF-8"
        }
      })
      .then(response => response.json())
      .then(json => {
        console.log('RESPONSE FROM POST : ', json);
      });
    }

    render() {
      return (
        <div className='addForm'>
        <h3>Modifier un article</h3>
        <form onSubmit={e => this.props.updateArtcile}>
            <input
              ref={title => (this.title = title)}
              type="text"
              id="title"
            />
            <textarea
              ref={body => (this.body = body)}
            />
            <button className='button button__from' type="submit">{SUBMIT}</button>
          {this.state.error === true ? <ErrorForm errorMessage={EMPTY_FORM}/> : null}
        </form>
      </div>
      )
  }
}

export default withRouter(ArticleList);
