import React, { Component } from "react";
import ErrorForm from '../components/error/ErrorForm'

class AddPost extends Component {
    constructor(props) {
      super(props);
      this.state = {
        error: ''
      }
    }

    _onSubmit = event => {
        event.preventDefault();
      if (this.title.value === "" || this.body.value === "") {
        this.setState({
          error: 'Ce champs est necessaire à l\'ajout'
        })
      }
      const body = {
        userId: 1,
        title: this.title.value,
        body: this.body.value
      };
      this.props.addArticle(body);
    }

    render() {
      return (
        <div>
          <h1>Ajouter un  nouvel article</h1>
          <form onSubmit={event => this._onSubmit(event)}>
            <div>
              <input
                className="form__input"
                ref={title => (this.title = title)}
                type="text"
                id="title"
                placeholder="Titre de l'article"
              />
              {this.state.error !== "" ? <ErrorForm /> : null}
            </div>
            <div>
              <textarea
                ref={body => (this.body = body)}
                placeholder="Text"
              />
              {this.state.error !== "" ? <ErrorForm /> : null}
            </div>
            <div>
              <input type="submit" value="Envoyer" />
            </div>
          </form>
        </div>
      );
    }
  }

  export default AddPost;
