import React, {Component} from 'react'
import ArticleListItem from '../components/Article-list-item'
import Loader from '../components/loader';
import AddPost from './AddPost';
import {API_URL, METHODS, LIMIT} from '../Constants'


class ArticleList extends Component {


    constructor(props){
        super(props)
        this.state = {
          data: [],
          loading: false
        };
        this.handleClick = this.handleClick.bind(this);
    }


    async componentDidMount () {
      try{
        const response = await fetch (`${API_URL}/posts${LIMIT}`);
        const json = await response.json();
        this.setState({data: json})
      } catch (error){
        console.log("ERROR" + error)
      }
    }

    async handleClick(index) {
      const { data } = this.state;
      const response = await fetch(`${API_URL}/posts/${index}` , {
        method: METHODS.delete
      });

      if (response.status === 200) {
        const filterData = data.filter(item => item.id !== index);

        this.setState({
          data: filterData
        });
      }
    }

    // async _addArticle(body) {
    //   try {
    //     const response = await fetch(`${API_URL}/posts`, {
    //     method: METHODS.post,
    //     body: JSON.stringify({
    //       userId: 1,
    //       title: body.title,
    //       body: body.body
    //     }),
    //     headers: {
    //     "Content-type": "application/json; charset=UTF-8"
    //     }
    //   })
    //   const json = await response.json()
    //     this.setState({
    //       data: [...this.state.data, json]
    //     })
    //   } catch (error){
    //     console.log("ERROR: " + error)
    //   }
    // }

    _addArticle = tab => {
      fetch(` ${API_URL}/posts`, {
    method: 'POST',
    body: JSON.stringify({
      userId: 1,
      title: tab.title,
      body: tab.body
    }),
    headers: {
      "Content-type": "application/json; charset=UTF-8"
    }
  })
  .then(response => response.json())
  .then(json => this.setState({
          data: [...this.state.data, json],
          loading: false
        }))

    }


    render() {
      if (this.state.loading) {
        return <Loader />;
      }  
    if(this.state.data !== []){
      return (


        <div className=''>
          <AddPost addArticle={this._addArticle}/>
          <ul className='dataList'>
            {console.log(this.state.data)}
              {this.state.data.map(el => (
                  <ArticleListItem 
                    key={el.id} 
                    title={el.title} 
                    body={el.body} 
                    onClick={() => this.handleClick(el.id)}
                  />
              ))}
            </ul>    
        </div>
      )
    }
  }
}

export default ArticleList
