import React, {Component} from 'react'
import ArticleListItem from './Article-list-item'
import Loader from '../loader/loader';
import AddPost from './AddPost';
import UpdatePost from './UpdatePost'
import {API_URL, METHODS, LIMIT} from '../../Constants'


class ArticleList extends Component {


    constructor(props){
        super(props)
        this.state = {
          data: [],
          loader: false,
          update: false,
          updateEl: {}
        };
        this.handleClick = this.handleClick.bind(this);
    }


    async componentDidMount () {
      this.setState({
        loader: true
      })
      try{
        const response = await fetch (`${API_URL}/posts${LIMIT}`);
        const json = await response.json();
        this.setState({
          data: json,
          loader: false
        })
      } catch (error){
        console.log("ERROR" + error)
      }
    }

    async handleClick(index) {
      const { data } = this.state;
      const response = await fetch(`${API_URL}/posts/${index}` , {
        method: METHODS.delete
      });

      if (response.status === 200) {
        const filterData = data.filter(item => item.id !== index);

        this.setState({
          data: filterData
        });
      }
    }

    // async _addArticle(body) {
    //   try {
    //     const response = await fetch(`${API_URL}/posts`, {
    //     method: METHODS.post,
