    //     this.setState({
    //       data: [...this.state.data, json]
    //     })
    //   } catch (error){
    //     console.log("ERROR: " + error)
    //   }
    // }

    _addArticle = tab => {
      this.setState({
        loader: true
      });
      fetch(` ${API_URL}/posts`, {
        method: 'POST',
        body: JSON.stringify({
          id: tab.id,
          userId: 1,
          title: tab.title,
          body: tab.body
        }),
      headers: {
        "Content-type": "application/json; charset=UTF-8"
      }
      })
      .then(response => response.json())
      .then(json => this.setState({
        data: [...this.state.data, json],
        loader: false
      }))
    }

    _updateForm = (id, title, body) =>{
      console.log(id)
    }

    render() {
      if (this.state.loader) {
        return <Loader />;
      }
      if (this.state.update) {
        return <UpdatePost updatedata={this.state.updateEl} />;
      }      
      if(this.state.data !== []){
        return (
        <div className=''>
          <AddPost addArticle={this._addArticle}/>
          <ul className='dataList'>
            {console.log(this.state.data)}
              {this.state.data.reverse().map(el => (
                  <ArticleListItem 
                    key={el.id} 
                    title={el.title} 
                    body={el.body} 
                    onClick={() => this.handleClick(el.id)}
                    onUpdate={() => this._updateForm(el.id,el.title, el.body)}
                  />
              ))}
            </ul>    
        </div>
      )
    }
  }
}

export default ArticleList
